INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703221444370', N'VMA学生参加广东省电子键盘艺术节获奖', N'19', N'huchun', N'2016-12-12 09:00:00.0000000', N'["VMA 双排键社和古典音乐社的同学共同排练合作参加广东省电子键盘艺术节获得了重奏组金奖和银奖，另外还获得最佳编曲奖，优秀组织奖，指导金奖。"]', N'["http://img.vma.edu.cn/image/20170322/6362579033358769506517619.png","http://img.vma.edu.cn/image/20170322/6362579034789901463393939.png","http://img.vma.edu.cn/image/20170322/6362579056914377111011785.png","http://img.vma.edu.cn/image/20170322/6362579058363665032373247.png"]', N'["","：广乐骘子建乏小們美得理音乐节","","一冖一∕/"]')
