INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703071541040', N'【海边语文课】海上明月共潮生', N'1', N'zhoujing', N'2017-02-14 15:40:00.0000000', N'["“春江潮水连海平，海上明月共潮生”，张若虚的《春江花月夜》流传千年，字里行间的美景和哲思引起无数人的共鸣。如何能让学生更深刻地体会这首经典诗歌中的诗情画意，并由景生情，进行哲学性的思考呢？万科梅沙书院语文组的吴老师利用学校临近大海的地理优势，在一个春寒料峭的夜晚，带着学生去海边感悟“春海花月夜”的诗意。","晚9时，师生们迎着点点星光，来到学校附近的海滩上。海上只能看见远处船上的几点灯火，海水跟随着风的步履，一浪一浪地向前翻涌而来。脚下就是一片在海水潮气中浸润的沙子，大家面朝着幽深的大海，踮起脚眺望着天边晦暗的云，想要从中找到月亮的踪迹，然而夜幕下只有星星俏皮地眨着眼睛，笑而不语。同学们开始议论纷纷，说好的月亮呢？这时吴老师观察了一下，“同学们，月亮就在海平线上，它马上就要升起来啦！”","果然，片刻之后，月在不经意间从云中露出了丝丝身影，若隐若现，浮着淡淡的红色。它穿过海水越过云层，拨开缕缕云雾爬上了夜空，周围还萦绕着红光。这令人想起了古时波斯的舞女，头上披着妩媚的红纱。月光浮在涨潮的大海上，任凭海浪翻涌，却依然光洁如初、清明透亮。同学们看到眼前这“海上生红月”的美景，纷纷惊叹不已。"," 伴着海上明月，大家一起大声朗诵起《春江花月夜》：“春江潮水连海平，海上明月共潮生。滟滟随波千万里，何处春江无月明……”朗朗的读书声伴着海浪，一直飘向远方，大家仿佛也随之穿越千年，回到了朝气蓬勃的初唐，和张若虚一起感受着月夜中的浪漫。","“‘江畔何人初见月，江月何年初照人。人生代代无穷已，江月年年望相似。’同学们，张若虚在几千年前面对着同样的月亮对生命、对宇宙进行了思考，那么现在你面对这如画的美景，又作何感想呢？”同学们望着逐渐明亮的月亮和涌动的大海，静静陷入了对宇宙、对生命的思考。","“诗者，志之所之也，在心为志，发言为诗。”","诗词源于生活，源于诗人看到美景时的感动，源于诗人强烈感情的迸发，诗词的学习不应该仅仅停留在课本上，课本之外那种心灵的触动，那种穿越时空的、人类永恒的情感和哲思，才是古诗词给予我们最珍贵的馈赠。也许很多年后，同学们已经背不出《春江花月夜》的诗句了，但他们仍然还记得那个春寒料峭的夜晚，那一轮伴着海潮升起的红月带给他们的感动和思考。"]', N'["http://img.vma.edu.cn/image/20170308/6362458222037770879288426.png","http://img.vma.edu.cn/image/20170308/6362458225131321022332276.png","http://img.vma.edu.cn/image/20170308/6362458227425211617877266.png","http://img.vma.edu.cn/image/20170308/6362458230287934737400662.png","http://img.vma.edu.cn/image/20170308/6362458232403130941866529.png"]', N'["","","","",""]')
GO
