INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703080815440', N'深圳市教育局郭雨蓉局长一行视察万科梅沙书院', N'1', N'huchun', N'2015-11-30 08:00:00.0000000', N'["11月30日下午，深圳市教育局郭雨蓉局长、社会力量办学管理处彭尧处长、基础教育处姚一勤处长、局办公室钟志红主任以及市教育科学研究院叶文梓院长莅临万科梅沙书院视察指导。深圳市万科房地产有限公司张酹副总经理、万科教育基金会唐鹭会长、万科梅沙书院王赫院长陪同，并对书院开办以来各项工作的进展情况座作介绍。","正值深秋，秋高气爽，空气分外清新，张酹副总经理等带领郭局长一行首先视察了书院的外围设施，重点视察了篮球场、足球场、开设划艇课程的愿望湖、学生食堂等场所。开学之前曾来书院检查工作的市局相关领导表扬了这三个月来书院发生的巨大变化，表示学生在这样的环境中学习和生活，心情应该分外舒畅。","随后，一行人沿着象征攀登理想高峰的书院七十三级台阶拾级而上，进入了书院教学区。沿途，市局领导视察了多功能展示大厅、VMAshop、班级教室、实验室、作品墙等众多展现书院精神和文化内涵的区域。郭局长对VMAshop的建设提出了建议，希望能更多的展示学生原创的创意作品。在多功能大厅的书架旁，郭局长仔细翻阅了书院《生涯规划手册》和书院介绍画册。王赫院长向郭局长介绍，梅沙书院图书馆有一门特别课程，即邀请每一位来到书院的领导和专家在画册上签上名字，作为校史资料珍藏。郭局长欣然在书院画册上写下对梅沙书院的殷切期望：梅沙书院，扬帆起航！做世界名校！","在教师办公室，郭局长亲切询问了中外教师在书院工作的感受，并鼓励老师们做好开局，树立信心，把书院建设成为世界名校！在班级教室，郭局长信手翻阅了一位学生关于“二胎政策”的时事评论习作，学生独特的视角和犀利的文笔让大家会心一笑。","在书院走廊的松木公告板前，张酹副总经理和唐鹭会长重点介绍了万科教育发展基金会针对万科梅沙书院发起的专项奖励计划——“辛巴计划”的实施情况，“辛巴计划”体现出万科人对教育事业的慷慨热诚和积极奉献。","在书院会议室，首先以一段视频介绍了梅沙学子开学以来学习与生活的成长点滴。这段视频充分展示了梅沙学子这三个月来的可喜变化，以及他们对学术的专注、对生活的热爱、对社会的关注与担当。接着，王赫院长从办学基本概况、办学理念、组织架构、课程的设置与实施、三年发展规划五个方面向局领导进行了工作汇报。唐璐会长向有关职能部门提出了在办学过程中遇到的困难，有关领导及时给予答复，表示会积极调研，帮助书院解决实际问题。张酹副总经理对市局给予梅沙书院的关怀和支持表示感谢，同时表示作为城市配套服务商的万科，将在高中、初中、小学教育体系内进行更广泛意义上的尝试。","最后，郭局长对万科集团与深圳中学强强联合的办学模式表示认可，认为这是深圳优质教育资源向社会辐射的成功尝试。同时，郭局长对梅沙书院的发展提出了期望，希望梅沙书院在进行国际化教育的同时，也要更多地进行国情教育，发挥国家基础教育的优势，为中国培养优秀人才！","郭雨蓉局长一行对梅沙书院的视察和殷切期望，让梅沙书院更加坚定了努力的方向：在中国的土地上，建设具有中国独特魅力的，具有国际卓越高中品质的世界名校！"]', N'["http://img.vma.edu.cn/image/20170308/6362455729659580187426224.png","http://img.vma.edu.cn/image/20170308/6362455743338816913596816.png","http://img.vma.edu.cn/image/20170308/6362455753869926381296318.png","http://img.vma.edu.cn/image/20170308/6362455756235407124524443.png","http://img.vma.edu.cn/image/20170308/6362455764638354175029600.png","http://img.vma.edu.cn/image/20170308/6362455768636856426211635.png"]', N'["","","","","",""]')
GO
GO
