INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703221015400', N'AP 2D学生作品 - 壁画', N'18', N'huchun', N'2016-11-05 10:00:00.0000000', N'["作者：谭博浚，吕诗彤，方中信","作者：王画，杜明昊，杜小奢","作者：张文清，徐云漫，张正昊"]', N'["http://img.vma.edu.cn/image/20170322/6362577437441495976537489.png","http://img.vma.edu.cn/image/20170322/6362577438313506142476147.png","http://img.vma.edu.cn/image/20170322/6362577440872955023268536.png","http://img.vma.edu.cn/image/20170322/6362577442924202043564594.png"]', N'["","","",""]')
