INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703100845400', N'开具成绩单政策', N'12', N'huchun', N'2017-03-10 08:37:00.0000000', N'["　　 只有学生本人或者学生的家长/法定监护人可以申请开具该生的成绩单。高中成绩单包含该生在梅沙书院读书期间内的所有成绩。学生信息和成绩记录不允许进行任何省略和更改。 为保护梅沙书院成绩单的诚信以及完整性，并保持对所有学生的公平待遇，升学指导部以及学业支持部会严格执行这项政策。","　　 官方成绩单只可以从梅沙书院直接寄给学院、大学、或其他高中。","　　 非官方成绩单可以邮寄或者直接交给学生或学生的父母/法定监护人","　　 申请开具成绩单流程","　　 学生可以直接到升学指导部(510室)填写和提交开具成绩单表格。","　　 学生可以通过邮件提交给升学指导部签字的开具成绩单表格。","　　 下载：开具成绩单表格","　　 官方成绩单开具通常需要5个工作日来处理。","　　 成绩单种类 　　 或 "]', N'[""]', N'[""]')
GO
GO
