INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703211234270', N'入学考试科目及时间', N'17', N'huchun', N'2017-03-21 12:33:00.0000000', N'["　　 每次考试均会择优录取部分学生，考生自愿选择考试时间。","　　 每场考试的第一天上午为笔试，当天晚上公布入围面试考生名单，第二天为面试。","　　学生可用托福考试成绩或SSAT考试成绩申请梅沙书院入学考试英语笔试免试，但需要达到以下条件：托福90分（含90）以上；SSAT高阶1800分（含1800）以上。"]', N'["http://img.vma.edu.cn/image/20170522/6363104877140444407573052.jpg"]', N'[""]')
GO
GO
