INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'20170221142357', N'Victory at 2016 International History Bee & Bowl (Shenzhen)', N'5', N'yumingmei', N'2017-02-21 14:23:30.0000000', N'["On November 26, Vanke Meisha Academy sent four teams (nineteen students in total) to theInternational History Bee  Bowl (Shenzhen) and won First Prize and Second Place in the Varsity Bowl, First Prize and Third Place in the Junior Varsity Bowl, a sweep of the top three prizes in Varsity Bee, and Second Prize and Third Place in Junior Varsity Bee. All of these students might be selected for the upcoming 2016-2017 Asia History Bee  Bowl tournament in Hong Kong.                      "]', N'["http://img.vma.edu.cn/image/20170105/6361922789451004989228042.png","http://img.vma.edu.cn/image/20170105/6361922789461924989728809.png"]', N'["","&I"]')
