INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703080850340', N'日本早稻田大学上海研究所杨军主任访问万科梅沙书院', N'1', N'sunenze', N'2016-04-19 20:40:00.0000000', N'["在日本，有一所拥有134年的历史，并与中国有深厚渊源的名校——日本早稻田大学，它培养了李大钊、陈独秀、廖仲恺、廖承志、彭湃、江户川乱步、村上春树等众多政经界、文化界的著名人士，索尼、卡西欧、三星、任天堂等众多著名公司的创始人及社长皆出身于早稻田大学。日本有8位首相毕业于此。","4月19日，早稻田大学上海研究所的杨军主任来到万科梅沙书院，与书院的老师就未来学术合作进行了沟通。杨军主任介绍了每年成功申请早稻田大学的中国学生的比例、背景和分布。他对是否应该在高中阶段为学生召开日语选修课提出了中肯的建议，并针对我院有意向申请日本大学的同学提供了非常有价值的讯息。此前，杨军主任曾多次造访深圳中学开展学术交流活动，据悉，深圳中学已有数十名高中学生被早稻田大学录取。","早稻田大学作为亚洲最为国际化的大学之一，注重学术的独立和学术的活用，并以造就模范国民为建校的宗旨。梅沙书院此次与早稻田大学就未来开展学术交流活动进行了初步探讨，通过与不同文化背景的国际顶尖名校进行切磋交流，以提升梅沙书院国际教育的敏锐度，以促进书院文化的多样性。","杨军主任非常认同万科梅沙书院办学理念、课程设置、师资建设，他对书院学生在国际比赛中取得优异成绩的同学给予祝贺，同时还为书院课程的长久发展和升学指导工作提出了建设性意见，期待与梅沙书院展开多领域的交流与合作。"]', N'["http://img.vma.edu.cn/image/20170308/6362455939949594613252304.png","http://img.vma.edu.cn/image/20170308/6362455949755089553892745.png","http://img.vma.edu.cn/image/20170308/6362455961918301534247985.png"]', N'["AV","",""]')
GO
