INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703082028010', N'市教育局范坤副局长、盐田区教育局李志利局长视察万科梅沙书院', N'1', N'sunenze', N'2016-09-07 20:15:00.0000000', N'["9月6日上午，深圳市教育局范坤副局长、盐田区教育局李志利局长一行来到万科梅沙书院进行开学初的工作视察和指导。随行的还有市教育局社会力量办事处彭尧处长、市教育局社会力量办事处邹敬忠科长以及盐田区基础教育科叶菲科长。深圳中学赵立校长、梅沙书院院长王赫、外方院长Peter Warsaw、万科教育发展基金会理事长、深圳市梅沙教育发展有限公司总经理唐鹭陪同视察。","范局长一行首先视察了书院的外围设施，重点视察了运动场地、学生宿舍楼和书院食堂。每到一处，范局长都仔细询问楼体的安全细节和应急通道。紧接着，一行人进入了书院教学区，视察了多功能展示大厅、VMAshop、班级教室、实验室、教师休息室等众多展现书院精神和文化内涵的区域。沿途，范局长亲切询问学生的校园生活和中外教师的工作情况，仔细观赏了梅沙学子形式多样的艺术作品，并对现代高端的实验室设备提出了一些问题和建议。盐田区教育局李志利局长对梅沙书院高目标、高品位的办学定位深表赞赏，并对梅沙书院发展成为国际一流高中、培养世界领袖人才的目标充满期待。","在随后的汇报会上，首先播放了两段视频介绍高一新生训练营和军训的场景，以及梅沙学子充实的一天，展示了梅沙学子阳光、专注、进取的青春风采。接着，王赫院长从办学基本概况、办学理念、课程的设置与实施、中外师资、书院生活等方面向局领导进行了工作汇报。唐鹭理事长代表万科表达了对教育事业的理想和情怀，并对办学中遇到的困难进行了汇报。深圳中学赵立校长感谢市局领导和区局领导在开学初的重要时段对梅沙书院的指导和关怀，同时汇报了深圳中学在教育理念、课程架构、学生活动、师资等方面给予梅沙书院的支持。","在听取了梅沙书院整体情况汇报之后，范局长对梅沙书院成立一年来的扎实工作给予肯定，并对今后的发展提出了殷切期望和明确要求。范局长对梅沙书院在安全办学、教学质量、宣传三个方面的工作提出了具体指导意见，表示梅沙书院的发展将会一如既往地受到市教育局和盐田区教育局的高度重视。最后，范局长对万科、深圳中学、盐田区教育局、书院全体教师所作的贡献表示感谢，希望能够加强优质企业和名校资源之间的合作，期待梅沙书院能够立足于中国，培养出具有“中国心”和国际竞争力的人才，推动深圳教育国际化的发展。在第32个教师节到来之际，范局长代表市教育局向梅沙书院全体教职工表达了节日的问候和祝福！"]', N'["http://img.vma.edu.cn/image/20170308/6362460141416222141436279.png"]', N'[""]')
GO
GO
