INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703150827421', N'联系升学指导', N'12', N'huchun', N'2017-03-15 08:24:00.0000000', N'["\n    使命与职责\n","\n\n    　　万科梅沙书院升学指导办公室旨在促进学生在高中的学业和个人发展 。学生在读期间，每个学生都会有一位升学指导老师对其进行辅导。\n","\n\n    　　 VMA升学指导老师是学生引领者。从高中课程的选择，到如何过渡到大学学习，以及毕业以后的计划，我们会在学生学习规划和大学申请方面的每一步帮助学生。我们会用一定的方法去全面了解学生，因为我们理解学术，个人和社会发展是相互交织的。我们与家长，顾问，教师，特殊教育者，心理咨询师或其他人合作，给予学生最全面的支持，帮助他们最大限度的发挥潜力。\n","\n\n    　　升学指导老师会与学生一对一面谈，或者采用小组授课的形式，并定期在全校范围内进行演讲。我们全方位的升学指导规划涵盖了许多主题：规划标准化考试与课外活动、职业与大学探索、简历的建立、了解不同国家大学的升学要求和申请程序、如何写个人陈述、邀请教师写推荐信等。从一对一学生面谈以及家庭面谈中发掘学生的兴趣，优势，成就和目标去帮助学生建立个性化的大学名单。\n","\n\n    升学指导办公室\n","\n\n    Kaylin Liang, 升学指导主任\n","\n\n    kaylinliang@vma.edu.cn\n","\n\n    (0755)6661 8486\n","\n\n    Letatia Brown Long\n","\n\n    letatiabrownlong@vma.edu.cn\n","\n\n    (0755)6661 8487\n","\n\n    周雅文\n","\n\n    zhouyawen@vma.edu.cn\n","\n\n    (0755)6661 8483\n","\n\n    徐菁玉\n","\n\n    xujingyu@vma.edu.cn\n","\n\n    万科梅沙书院510办公室\n","\n\n    广东省深圳市盐田区环梅路33号\n","\n\n    \n \n\n    \n \n\n    \n \n\n    \n \n\n    \n \n    \n "]', N'[""]', N'[""]')
GO
GO
