INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703241118490', N'以热爱为事业 家校携手共筑学子梦想之路', N'26', N'yumingmei', N'2016-07-01 23:55:00.0000000', N'["—记万科梅沙创业俱乐部第二次校内活动   ","期末考试前的最后一次社团课上，万科梅沙创业俱乐部（VANKE MEISHA ENTREPRENEURS CLUB）举行了本学期第二次校内创业大讲堂活动，讲座嘉宾、华傲大数据学院贾西贝博士为在场师生带来了以“一去一回的创业之路：留学与海归的人生闭环”为主题的讲座。历时九十分钟的讲座里，贾博士从留学背景、留学生涯、毕业抉择和海归创业四个方面生动详细地介绍了出国留学的关键因素与自己的创业经验。出国留学是一场走出象牙塔的历险，保持良好、积极的心态，突破自己的舒适区，以广阔的国际化视野审视这个世界，方能真正领略世界公民与领袖人才的担当；同样，创业也不是一场快节奏的头脑风暴，而是将热爱变成事业的过程，唯有充满澎湃激情，才能以持续的动力鼓舞自己朝梦想迈进。创业的成功，需要市场，人才，资金的高度协调，需要对见识和机遇的长期积累与把握，更需要领导者对自我以及企业的信心。明确自我目标，及早准备、抓住机遇并为之奋斗，才能成就乐于为之奉献的事业。","万科梅沙创业俱乐部是书院新兴的代表性社团之一。该社团由学生家长发起、学生自主管理并邀请书院教师担任校内指导老师，旨在帮助有志于出国攻读经济、商科与信息技术类专业的同学提前获取背景知识、通过图式建构及亲身实践增强整体竞争力，实现全面发展，打造未来创业潜质。社团日常活动主要分为两类：一是定期邀请知名企业家来梅沙书院进行演讲，激发书院学生的学习热情与创业思维；二是提供课外企业（均为国内外知名企业）实际工作环境体验机会，在实践和互动交流中为学生的梦想之路添砖加瓦。此外，社团也将不定期举办公益活动，培养学生的爱心与责任感。","除了在校的社团活动，鼎力支持书院工作的家长们也多次利用自身职业及社会资源，组织了各类校外企业分享与实践活动，邀请留学归国的职场精英为梅沙学子分享求学与入职的心路历程（如探索职业梦想——走进中国顶尖投行“中金公司”活动），使有志于从事相关行业的同学们得以触摸梦想、确立目标，描摹未来的形状；实操类项目的亲身体验更令孩子们受益匪浅。","书院成立至今，众多家长对校内工作予以肯定和赞许，并积极推进学生深入社区、社会的实践活动，鼓励他们挖掘自我潜能，增进与梦想职业从事人员的交流与沟通，方能筑梦踏实，稳步向前。在此，由衷感谢家长们对书院的支持与无私的资源分享！让我们携手为学生的成长提供更多选择与帮助，共筑梅沙学子通往未来事业与梦想的阳光之路。"]', N'["http://img.vma.edu.cn/image/20170307/6362449298908394668202043.png","http://img.vma.edu.cn/image/20170307/6362449311264869135433811.png","http://img.vma.edu.cn/image/20170307/6362449318802278385986218.png","http://img.vma.edu.cn/image/20170307/6362449323075872466616713.png"]', N'["","","",""]')
GO
