INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703241120540', N'Welcome to our party！万科梅沙书院首次万圣节活动', N'26', N'yumingmei', N'2015-10-31 23:55:00.0000000', N'["10月31日是西方节日中的万圣节。为增进对西方节日与习俗的了解、渲染万圣节气氛，我校于10月28日举行了首次完全由学生自我组织的大型校园文化活动，“The Most Scared Face”——万科梅沙书院万圣节Party。本次活动由高一四班宣传委员付钰淇与翟梓晶同学发起，她们不仅做好了活动策划，更筹集资金购买活动用品、协调活动时间及地点、多次进行宣传，于短短两周内获得了全体师生的支持与配合，使得活动顺利开展。","当天下午，书院的同学们齐心协力布置校园：大厅走廊挂起了万圣节主题装饰，原本色彩简单明快的教室也在同学们的精心布置后充满了“诡异”的气氛。在老师的协助下，同学们还倾力打造了“神秘的三楼”等待大家去试胆。活动伊始，书院外教老师为我们讲解了万圣节的文化与背景知识，其他外籍教师还时不时配合着台上的老师给专注聆听故事的同学们一点小“惊吓”。这一活动使得大家对万圣节的含义有了新的理解：原来它并不仅仅意味着南瓜灯、吸血鬼和万圣节的起源其实是人们赞美秋天的节日，西方的人们也在这天纪念圣人，寄托对逝去亲人的哀思。听完简短的介绍，大家在主持人的引导下前往“神秘的三楼”。密密麻麻的蜘蛛网，怪诞的音乐伴奏，不少同学在灯光昏暗的走廊里被窗边扮鬼的老师们吓了一跳。走廊尽头，老师们为各位同学准备了万圣节知识拷问活动，答对问题者更收到了精美的小糖果作为奖品。","经过紧张刺激的“百鬼夜行游三楼”活动，同学们齐聚展示大厅，进行万圣节最佳装扮的展示和评选。各式各样的奇装异服令人目不暇接，有可怕的吸血鬼、俏皮的小女巫、滑稽的海盗、让人意想不到的“绝命毒师”、大家熟悉的哈利波特、还有霸气的团。比赛规定先由每个班级推选出两名最佳装扮，在大厅上演华丽丽的，并由所有的同学通过扫描二维码进行投票。统票期间，大家观看了一小段恐怖电影，并在现场的欢乐音乐中合影留念。最终，六班同学集体扮演的团”在激烈的竞争中脱颖而出，夺得了万圣节装扮比赛的冠军。","本次万圣节活动完全由学生自己策划组织，因而每位同学都享受过程、乐在其中。它让我们在繁重的学业与工作中获得放松，在放松的同时获得了体验西方文化、节日和习俗的机会。大家在活动中一起装扮教室、一起鬼屋试胆、一起扮鬼脸拍照，大大增进了同学们之间的友谊与默契。每个“第一次”都难尽善尽美，但这必然是一个良好的起点，在全体师生的支持与参与下，梅沙书院的各项校园活动一定会精彩丰盛；三年的梅沙岁月，必将成为同学们人生中一抹绚丽的回忆！"]', N'["http://img.vma.edu.cn/image/20170307/6362448192608242498104492.png","http://img.vma.edu.cn/image/20170307/6362448197804802491885731.png","http://img.vma.edu.cn/image/20170307/6362448198829722494919137.png","http://img.vma.edu.cn/image/20170307/6362448200397622491175819.png","http://img.vma.edu.cn/image/20170307/6362448202124543598040576.png","http://img.vma.edu.cn/image/20170307/6362448205556565599473451.png","http://img.vma.edu.cn/image/20170307/6362448206725113093859035.png","http://img.vma.edu.cn/image/20170307/6362448207358477158534668.png","http://img.vma.edu.cn/image/20170307/6362448208984000974447835.png","http://img.vma.edu.cn/image/20170307/6362448210236680973543205.png"]', N'["","","","","","","","","",""]')
GO
GO
