INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703080838240', N'哥伦比亚大学教育学博士讲座于万科梅沙书院圆满举行', N'1', N'huchun', N'2015-12-12 08:00:00.0000000', N'["12月8日及11日，哥伦比亚大学教育学博士林雪娇女士到访万科梅沙书院，分别面向书院教师团队及全体学生开展专题讲座。周二，教师团队就“如何进行反思性写作”这一教学问题进行了探讨；周五下午放学后，书院全体学生则聆听了以“如何提升全球化能力以应对机遇和挑战”为主题的讲座，并现场与讲者互动。两场讲座主题各异，但同样令人受益匪浅。","12月8日，林雪娇女士在书院219教室介绍了在教学中运用反思性写作评估学生学习成效的方法。她首先介绍了反思性写作对于学生的意义：联系所学内容与已知内容，提高解决问题和批判性思维的能力，对所学的知识具有所有权。随后，她详细阐述了课堂内反思性评估模式Journal entry、Exit ticket、3-2-1和Weekly check-ins的使用方式，并就长期性的反馈写作如反思性日记、反思性写作及作品集的实施方式及写作要点进行了举例说明。林雪娇女士指出，在课堂中运用反思性写作的关键在于向学生提供明确的写作指引，并展现清晰的评估方式，方能令学生循序渐进，养成及时反思、对照、自我评估的良好学习习惯。","12月11日，哥伦比亚大学教育学博士林雪娇女士（Cindy Lin）以“00后的你，如何塑造国际竞争力？”为题，于青云报告厅举行了关于全球化能力的讲座。书院全体师生及部分学生家长到场聆听了本次讲座，并积极参与现场互动。","林雪娇女士首先阐述了全球化能力对梅沙学子们的重要性——对于即将出国求学的同学来说，适应不同的环境与文化是及其重要的。我们应抱持不批判（NON-JUDGE-MENTAL）、开明（OPEN-MINDED）、接受不同（ACCEPT DIFFER-ENCES）的心态，接受和理解不同文化的方方面面，了解他们的习俗与特色。面对全球化的环境，更应具备适应性和灵活性（Adaptable  flexible in unfamiliar situations），加深对国际意识和知识的理解（International Awareness, Knowledge,  Understanding），吸收在多元文化环境下生活、处事的经验（Experienced in Multicultural Environments），培养跨文化、跨语言界限的有效沟通能力（Effectively communicates across cultural  linguistic boundaries）。同学们需要走出自己的舒适区，通过看电影、阅读小说、学习语言、了解新闻和潮流等方式来学习其他文化，最终做到换位思考，真正地理解多元社会与内涵交融的意义。","现场提问环节中，有同学就在美国可能出现的人种歧视问题进行了提问。林雪娇女士回应，只要充分尊重并包容各类文化，和谐共处是可以实现的。另一位家长则提出了中国学生在美国的人际交往问题，林雪娇女士表示，学生应对自我的未来发展有清晰认识，有意识地挑战自我，加强与非中国学生的接触。","林雪娇女士在哥伦比亚大学主攻组织发展与领导力博士专业。在针对师生举办的两场讲座中，致力于树立全局观念，令师生更有条理地安排课堂教学与学习，培养适应竞争环境的各项能力，为老师们今后进行教学评估的方式和同学们的学习重点与方向进行了有效的指引。万科梅沙书院也将把全球化能力包含的“探索世界、交流想法、付诸行动、站在他人角度考虑问题”四个维度的要求融入到整体课程与活动、竞赛体系中，帮助同学们提高全球化能力，以适应未来竞争激烈的国际化社会。"]', N'["http://img.vma.edu.cn/image/20170308/6362455814747316321637611.png","http://img.vma.edu.cn/image/20170308/6362455830954156245873647.png","http://img.vma.edu.cn/image/20170308/6362455835205892898574602.png","http://img.vma.edu.cn/image/20170308/6362455871405118578248230.png","http://img.vma.edu.cn/image/20170308/6362455879220233176776515.png","http://img.vma.edu.cn/image/20170308/6362455885459042742305546.png","http://img.vma.edu.cn/image/20170308/6362455889676140775036219.png","http://img.vma.edu.cn/image/20170308/6362455895004615293684495.png","http://img.vma.edu.cn/image/20170308/6362455896840723475945239.png","http://img.vma.edu.cn/image/20170308/6362455898932682997183395.png"]', N'["","1\\\\","\\","","","","","QQ&h:im","",""]')
GO
