INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703201649280', N'2016VMA篮球风云', N'6', N'yumingmei', N'2017-03-20 16:48:41.0000000', N'[" 春日四月雨纷纷，梅沙书院的篮球少年们无数次惆怅地望着窗外的大雨，终于在4月26日迎来久违的艳阳天。"," 还等什么？继4月20日的初赛之后，26日下午梅沙篮球赛场上再起风云。3、8班联队vs4、5班联队的季军排位赛和1、2班联队vs6、7班联队的冠军争夺赛即刻打响。无人机已在球场上空嗡嗡作响，快看前方小伙伴发来的线报！","首先是3、8班联队（白色球衫）vs4、5班联队（红黑色球衫）的季军排位赛，先礼后兵，赛前先合个影吧。","    我是23号！谁敢拦我！","   这身姿！能给个正脸吗?","    4、5班最后以三分的弱势惜败给3、8班，没关系，在一起欢笑比奖杯更珍贵。","   季军排位赛的硝烟还未散去，1、2班联队（蓝色队服）vs6、7班（绿色队服）联队的冠亚军之争马上打响。赛前秀秀我们特制的队服，帅到无边际。","   开球啦，全场一片肃杀之气。","   眼镜哥，你往哪里跑？","   哈哈哈，眼镜哥也有必杀器，球~进~啦！","   最终，1、2班联队以26:19的压倒性优势赢得本次VMA篮球赛男子组的冠军，6、7班获得亚军。","    热血青春的篮球盛宴难道只是男生的专利吗？在4月20日男子初赛之后，被同学们戏谑地称为“4楼5楼大比拼”的女子篮球赛同样精彩激烈。","1、2、3、4班（四楼小分队）VS 5、6、7、8班（五楼小分队）","   看！她们在赛场上放开脚步去奔跑、传球、投篮，眉宇间没有丝毫胆怯。","   比赛完，美少女战士终于可以拍个照啦。喂~~后面的那几位是怎么回事？","   最后，让我们晒晒VMA篮球赛风云榜吧","   男子冠军队：1、2班联队","   男子亚军队：6、7班联队","   男子季军队：3、8班联队","   女子冠军队：5、6、7、8班联队","   男子最佳球员:卢振华","   女子最佳球员：经天纬","撰稿学生：徐云漫、曾子珊、董宇然、黄一霖、申慧、刘星辰（万科梅沙书院校园媒体中心·杂志部）","照片来源：万科梅沙书院摄影社"]', N'["http://img.vma.edu.cn/image/20170308/6362456068635522077025284.png","http://img.vma.edu.cn/image/20170308/6362456079502701516655320.png","http://img.vma.edu.cn/image/20170308/6362456087835083756651752.png","http://img.vma.edu.cn/image/20170308/6362456094986737076588956.png","http://img.vma.edu.cn/image/20170308/6362456103352195983610506.png","http://img.vma.edu.cn/image/20170308/6362456112485749141862460.png","http://img.vma.edu.cn/image/20170308/6362456120477441982469867.png","http://img.vma.edu.cn/image/20170308/6362456132075280929915629.png","http://img.vma.edu.cn/image/20170308/6362456141370240922532320.png","http://img.vma.edu.cn/image/20170308/6362456148002980927175787.png","http://img.vma.edu.cn/image/20170308/6362456156813300922996440.png","http://img.vma.edu.cn/image/20170308/6362456161886589404423968.png","http://img.vma.edu.cn/image/20170308/6362456167173934738588636.png"]', N'["","","","","","","","","","","","",""]')
