INSERT INTO [dbo].[articals] ([id], [title], [contenttype], [author], [publishtime], [content], [content-img], [content-img-trans]) VALUES (N'201703301619550', N'万科梅沙书院2016消防演习顺利举行', N'26', N'huchun', N'2016-01-18 23:00:00.0000000', N'["2016年1月18日下午，第八节课的下课铃声尚未响起，尖锐的火警警报便划破了教学区的宁静，万科梅沙书院首次教学区消防演习就此拉开序幕。灯灭铃响的刹那，全体师生反应迅速，按照书院消防工作安排中拟定的逃生出口有条不紊地撤离至足球场，并在最短时间内以班级为序整队、清点人数。消防演习全过程用时2分56秒，反映出梅沙学子对演习积极的应对态度及对逃生方法的正确掌握。演习后，书院院长助理黎治国老师就演习概况进行了总结；随后，书院物业部门及梅沙街道办消防队以宣传海报和实操演练的方式向同学们细致讲解了火灾逃生方式及手持干粉灭火器使用方法，为本次消防演习划下了圆满的句号。","应急避险演习与紧急疏散演练是书院日常安全工作与保障的必要组成部分。除了牢记各类逃生技巧，逃生器具的熟练使用更应当作为每一名学生的必备技能。因此，书院特别邀请梅沙街道办消防队专业人士来校，现场示范逃生器具的使用方法，并在他们的保护与指导下，为学生提供动手实践的机会。演示开始前，同学们围着场地护栏站成一圈，认真聆听手持干粉灭火器的使用步骤：打开铅封、拔掉保险栓，上下摇晃灭火器使内容物分布均匀，然后将喷口对准起火部位，喷！护栏边，十多个灭火器一字排开，现场同学们跃跃欲试，一个接一个拿起装备演练，姿势专业，态度认真；尚未排到的同学则在消防知识宣传板前流连，仔细阅读各类逃生技巧和注意事项，更在消防员的指导下试用其余逃生器具；一同参与演习的外教也加入了同学们，兴致勃勃地加入研究与讨论，气氛热烈而融洽。","本次消防演习在快速有序的疏散、紧张高效的整队与寓教于乐的科普中圆满结束。书院全体师生以将本次演习为契机，提升安全意识，进一步落实各项安全工作，为下学期即将进行的教学区地震应急演练与宿舍区消防应急演习作好知识与心态上的准备，营造一个平安、和谐的校园环境！"]', N'["http://img.vma.edu.cn/image/20170307/6362449440958226389227638.png","http://img.vma.edu.cn/image/20170307/6362449455006875387299046.png","http://img.vma.edu.cn/image/20170307/6362449474538813745626803.png","http://img.vma.edu.cn/image/20170307/6362449484458076613419894.png","http://img.vma.edu.cn/image/20170307/6362449496966775169218993.png","http://img.vma.edu.cn/image/20170307/6362449504188579158311915.png","http://img.vma.edu.cn/image/20170307/6362449510491875477294836.png","http://img.vma.edu.cn/image/20170307/6362449538962982438851793.png","http://img.vma.edu.cn/image/20170307/6362449549540614158465210.png","http://img.vma.edu.cn/image/20170307/6362449554439334152042859.png","http://img.vma.edu.cn/image/20170307/6362449561548765622510548.png","http://img.vma.edu.cn/image/20170307/6362449567990711572346414.png","http://img.vma.edu.cn/image/20170307/6362449573837262898517672.png","http://img.vma.edu.cn/image/20170307/6362449580641054297025108.png","http://img.vma.edu.cn/image/20170307/6362449585999053357116249.png","http://img.vma.edu.cn/image/20170307/6362449592178703134461378.png"]', N'["","","","","","","","","","","","","","","",""]')
