INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'22', N'主页', N'万科梅沙书院电话？', N'最后，万科集团总裁郁亮发表了精彩讲话，他谈到虽然深圳的经济发达，竞争力在中国排名第一，但教育和医疗资源的缺失制约着城市的进步与发展，万科作为一个有社会责任感的企业，我们有理想，也有实力能够办好学校，与深圳中学的强强联合也一定能够为深圳培养一批领袖型的优秀人才', N'2017-06-30 14:09:50.5133333')
