INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'118', N'哈哈哈哈哈', N'哈哈哈哈哈？', N'中国大智汇创新研究挑战赛，简称 CTB，是中国最大的高中生在线学习竞赛，由哈佛大学和清华大学于 2012 年联合发起，联合耶鲁、斯坦福、麻省理工、北大、复旦、交大等国内外近百所顶尖大学机构教授专家一起，为中国中学生精心设计百余个研究课题，以在线挑战赛、现场答辩赛和北美路演赛等形式寻找最具独立学习能力、独特思维方式和独创实践方案的出色中学生，并帮助他们实现世界一流大学的升学梦想', N'2017-06-30 16:01:01.4266667')
GO
