INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'392', N'我喜欢你蒋老板', N'我喜欢你蒋老板？', N'罗婉萌：有的人问我为什么喜欢舞蹈，我只能告诉他我喜欢站在舞台中间享受聚光灯的感觉，就是这一刻，无论有观众与否，我都愿意付出加倍的汗水去耕耘，去收获', N'2017-07-01 13:04:37.4300000')
