INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'395', N'per', N'per？', N'在整体课程框架的指引下，语文教师邬宁馨、数学教师Per Nilsson、英语教师Marco及物理教师陈少锐对人文、数学、英语、自然科学等四大领域的课程结构、学术活动、国际竞赛等方面作了进一步介绍', N'2017-07-01 13:09:35.9166667')
