INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'708', N'你这答案也太差了吧', N'你这答案也太差了吧？', N'你希望三年后，有一个很精彩的履历，考上一个心仪的学校；你希望人生大有作为，生活精彩而幸福；那么，来吧，在开始你国际高中生活的时候，送给自己一个礼物，写下高中毕业离开梅沙书院时自己要实现的目标，然后一步一步走近她，每一个目标就会成为你登高的阶梯，作为梅沙书院的长子、长女，把你的经历、你的历史、你的奋斗、你的成长留给学弟学妹们', N'2017-07-02 11:13:32.4100000')
