INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'711', N'学校简洁', N'万科梅沙书院简洁？', N'万科梅沙书院是由万科教育发展基金会举办，委托深圳中学办学及管理的优质国际化学校。书院是深圳市教育局审核批准的民办学校，并且具有广东省教育厅授权的进行国际课程实验资质。', N'2017-07-02 11:39:51.8633333')
