INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'717', N'学生事务部', N'学生事务部？', N'2017年是微软“创新杯”赛事举办的第15年，本届赛事吸引了近4000名中国学生报名参与，782个各类创新的项目方案经过初赛以及各省、市、校区赛的层层评审，超过310个项目成功进入复赛阶段的筛选；经过复赛及半决赛的残酷淘汰，最终仅有29所高校的27支团队入围中国区总决赛，而能代表中国参加全球总决赛的团队只有1个', N'2017-07-02 11:45:49.2600000')
