INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'720', N'外联部电话', N'外联部电话？', N'尽管蒋亦韬与他的团队止步于全国三等奖，但作为唯一一个参加了2017微软“创新杯”并进入决赛的高中生，能够与来自北京大学、上海交通大学、西安电子科技大学的全国精英们同台竞技，他已经在这次参赛中收获良多，也一直热情鼓励对赛事和项目感兴趣的同学一起参与到组队和研发中来', N'2017-07-02 11:46:45.5266667')
