INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'722', N'陈潇', N'陈潇？', N'陈潇；女；中教；社会科学组组长，生涯规划课教师；教师团队；硕士：伦敦政治经济学院（LSE）社会与文化心理学专业rnrn本科：英国杜伦大学心理学专业rnrn硕士毕业于伦敦政治经济学院（LSE）社会与文化心理学专业，获英国咨询心理学协会认证的咨询资格证书。在一对一咨询、小组治疗、儿童与青少年教育方面有着丰富经验。擅长中英双语教学。', N'2017-07-02 11:47:52.7933333')
