INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'726', N'新生训练营都训什么', N'新生训练营都训什么？', N'来自高一九班的罗婉萌和高一十班的余天宇，真诚分享了他们在新生训练营中的收获和体会：第一次离家、过上集体生活的罗婉萌在学长团的帮助下迅速适应了新环境、融入了新集体、交到了新朋友，对梅沙有了满满的爱和归属感；通过六天的新生训练体会到自我不足的余天宇，则深深明白了No Pain，No Gain的道理', N'2017-07-02 11:56:38.5300000')
