INSERT INTO [dbo].[questionlog] ([id], [question], [req], [answer], [time]) VALUES (N'730', N'Davian是谁', N'Davian是谁？', N'Davian Ricardo Chin；男；外教；数学科组长，数学教师；教师团队；本科：西印度群岛大学数学与计算机专业rnrn专业证书：教育心理学，教学技能，在线学习数学和物理教师资格证rnrn拥有8年数学和物理教学经验，在数学和高级物理领域有着广博的知识。他曾在St. Hugh’s高中女校担任数学教师（A—Level），同时辅助数学与物理的AP教学。他曾在纳扎尔巴耶夫物理和数学学院担任11年级和12年级学生剑桥A-level课程教学。曾在国际上极具影响力的科学期刊European Physical Journal C — Particles and Fields上发表了研究成果。', N'2017-07-02 11:57:43.2666667')
