INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'1', N'王赫', N'院长（中方院长） 学业支持部主任', N'管理团队', N'美国南加州大学, 国际教育管理博士(在读); 东北师范大学文艺美学硕士; 万科梅沙书院院长。曾任深圳中学国际部主任、学生处主任、团委书记等。2011年担任深圳美育研究所所长，主持广东省十二五课题项目省级重点课题。深圳市教育局海外培训项目美国布朗大学教育系进修班成员， 2013年赴美国哈佛大学校长中心进修。2015年《教师月刊》第一期封面人物。', N'2')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'2', N'Peter Warsaw', N'外方院长（外教） 音乐老师', N'管理团队', N'毕业于哈佛大学及伊斯曼音乐学校（全球排名第一），曾任美国顶级私立高中迪尔菲尔德学院学术院长，30多年教学管理经验； 音乐教学专家，钢琴大师 ，创办世界级青年交响乐团；曾任圣保罗学校艺术课程顾问、剑桥考试ETS AP 音乐理论主试人、美国大学理事会AP考试理事会成员。', N'1')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'3', N'姜寿莉', N'学生事务部主任', N'管理团队', N'毕业于东北师范大学物理系。曾任深圳中学国际体系负责人、AP物理教师。曾为美国青年物理学家锦标赛深中队指导教师，获物理竞赛优秀指导教师称号，多次被评为深圳中学十大最受学生欢迎的教师。曾为深圳美育研究所成员。曾赴美国圣文森山大学（Mount Saint Vincent University）进修学习。', N'2')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'4', N'郭宇', N'招生及对外联络部主任', N'管理团队', N'毕业于东北师范大学英语专业，第五届全国青少年英语技能大赛深圳赛区朗诵项目优秀辅导金奖；2009中国全国青少年英语全能王大赛深圳赛区中小学组优秀辅导奖；2012年度深圳市教育系统先进教师；2014年深圳特区报评选活动中获得“深圳好老师”称号。', N'2')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'5', N'Kaylin Liang', N'升学指导部主任', N'管理团队', N'硕士（在读）：乔治华盛顿大学教育与人类发展专业；学士：加州大学伯克利分校社会学；大学升学指导课程：莱斯大学。 拥有多年在知名国际学校担任升学指导老师和舍堂主任的工作经验。擅长指导学生的职业规划、申请文书的准备，举办大学申请介绍会，组织策划学生活动和竞赛，带领学生进行户外调研。也曾任教于加州奥克兰市非盈利教育机构: College Track。', N'2')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'6', N'李显耀', N'学生事务部副主任', N'管理团队', N'毕业于华中科技大学国际经济与贸易专业，深圳中学国际课程体系创建工作组成员，国际部办公室工作逾六年，任升学指导工作，熟悉学生学情及升学趋势，能够提出具体而有指导性的升学建议。', N'1')
GO
