INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'7', N'张莉', N'行政部主任', N'管理团队', N'毕业于华东政法学院国际法系，曾为深圳中学国际课程体系创建工作组成员，在深圳中学国际部办公室、校长办公室工作逾六年。2015年6月被深圳中学外派万科梅沙书院行政部工作。', N'2')
