INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'12', N'Elliot A. Rawdin', N'英语教师', N'教师团队', N'学士：英国兰卡斯特大学英国语言文学及创意写作专业

TEFL国际英语教师资格证

毕业于英国兰卡斯特大学英国语言文学及创意写作专业。专注现代文学，先后任职于英国教育出版机构及当地国际学校，曾被深圳市教育局下派公办中学担任英语教师，创设外教英语课程体系。', N'1')
