INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'13', N'Kindra Gunn', N'英语教师', N'教师团队', N'硕士（在读）：德州大学阿灵顿分校文学研究专业，

学士：德州大学阿灵顿分校英语文学艺术专业，

英语教师资格证和国际英语教师资格证

拥有3年在德州North Crowley High School in Mansfield 的英语教学经验，曾任教11年级的英语老师，并为9年级至12年级规划课程，这一经历使得她在教学能力上得到发展。在课堂上, 她注重帮助学生了解美国文化,进行英语文学艺术和语法的教学活动。她乐于利用课外时间指导学生写作、分析与论证能力。', N'2')
