INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'14', N'Steve Gamble', N'英语教师', N'教师团队', N'学士：旧金山州立大学工商管理专业。

拥有11年的英语教学经验，精通美国Common Core课程计划的英语语言艺术标准，帮助学生进行深度学习。他曾在法国的学校担任英语学科组长4年，期间，也辅助学生升学指导，为法国学生举办英语文化交流活动，他曾在上海一所国际学校任教9年级和10年级的Common Core-aligned curriculum，兼任文化交流活动的顾问。', N'1')
