INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'15', N'Lisa Knight-Sutton', N'公共演讲教师', N'教师团队', N'硕士，The University of Western Ontario美国文化研究专业，

