INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'20', N'鲁滔', N'SAT/TOEFL教师', N'教师团队', N'毕业于长春工业大学英语语言文学专业

芝加哥东西大学进行交换学习，获国际关系学士学位

