INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'21', N'沈炜楠', N'SAT/TOEFL、英语教师', N'教师团队', N'硕士：伦敦大学学院(UCL)英语语言学专业。

英语专业八级，担任过高考英语科目口语副主考官。曾在重点中学和大学任英语教师。耐心细致，乐于帮助学生解答学习上的问题和困惑。英语口语地道，专业功底深厚，很敬业，热爱学生，注重学生的全面发展，认为教师最大的幸福就是看到学生们不断成长并到达理想的彼岸。', N'2')
GO
