INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'22', N'周飞', N'深圳中学语文教师、语文科组长', N'深中导师团队', N'从事语文教学20年，教学中力求通过文字来解读自然、社会与人生，风格自然、平易，个性色彩浓烈，获国家级、深圳市教学大赛一等奖各一次；教育管理中力求深入学生内心，所带班级学风纯正，成绩优异。', N'1')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'23', N'茆伟', N'SAT/TOEFL、英语教师', N'教师团队', N'硕士：武汉大学对外汉语专业

曾在南昌大学共青学院任教大学英语听力精读泛读课程五年，广东碧桂园学校国际高中任教国际项目英语和雅思课程三年，在碧桂园十里银滩学校初中部任MYP项目助理协调员和七八年级英语课程一年，北京新东方扬州外国语学校国际高中任教托福一年。

曾指导学生获2006,2007年CCTV杯全国英语演讲比赛大学组江西赛区一等奖、三等奖，曾带队参加哈佛大学模拟联合国与四川凉山州偏远山区支教。两篇教育论文分获佛山市顺德区2015年论文评选一等奖，二等奖。', N'1')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'24', N'邬宁馨', N'语文科组长，语文教师', N'教师团队', N'硕士：香港大学中国语文教育专业

研究生阶段考获香港教师语文能力评核全科满分，并于香港大学教育学院中文教育研究中心担任学生研究助理', N'2')
