INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'25', N'吴祎', N'语文教师', N'教师团队', N'硕士：华中师范大学中国语言文学专业

曾任教于新加坡政府中学，有多年的国际中文教育经验；合著《民俗文化与创意产业》、《2007中国民间文艺学年鉴》，选编《深林奇谈》等书。', N'2')
