INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'29', N'陈潇', N'社会科学组组长，生涯规划课教师', N'教师团队', N'硕士：伦敦政治经济学院（LSE）社会与文化心理学专业

