INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'30', N'Adam John Privitera', N'心理学教师', N'教师团队', N'心理学教学协会，普通心理学协会，美国心理学会，本科神经科学教学委员会专家成员；曾以心理学与神经学教授的身份，任教于专科和大学本科阶段。他拥有包括学科组长在内的7年教学经验，此外，他也曾担任期刊 Teaching of Psychology 和美国心理协会年会刊物的审稿人。其教学侧重于探究式学习，培养创造性思维，尊重个性化发展和文化特质。', N'1')
GO
