INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'32', N'唐芯雅', N'心理咨询教师', N'教师团队', N'硕士，加拿大维多利亚大学教育心理学

硕士毕业于加拿大维多利亚大学教育心理学专业，在处理青少年心理健康问题方面有着丰富经验。本科阶段曾在深圳康宁医院和蛇口育才第三小学实习。此外，她也是深圳市人民法院青少年法庭的志愿者（专注心理健康）。', N'2')
GO
