INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'33', N'Kimberly Christine Garner', N'美国历史教师', N'教师团队', N'硕士，摩斯大学语言艺术与社会学专业，

语言艺术和社会学的教师资格证

曾在华盛顿的查维斯公立学校执教AP美国历史和美国历史，同时出任11年级的教学组长。在2014年3月和2015年8月，她以卓越的教学技能和对学生课外学业成绩的贡献，被评为“优秀教师”。她在分析学生成绩数据等方面经验丰富，擅于使用有效教学方法来辅助教学。', N'2')
GO
