INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'35', N'刘凯', N'历史教师', N'教师团队', N'博士：中山大学历史学系隋唐史与敦煌学专业（硕博连读）

中山大学历史学系博士（硕博连读），在读期间多次获得校级和国家级奖学金；获得第六届“全国史学新秀”三等奖；担任《中山大学研究生学刊》历史学科编辑、总编等职务。主要研究和关注的领域为中国古代宗教史，历史地理与区域史，战争史等。', N'1')
GO
