INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'63', N'许美杰', N'体育教师', N'教师团队', N'学士：成都体育学院民族传统体育武术专业

现为武术套路二级运动员，武术段位制五段持证员，四段指导员、考评员，国家武术套路项目一级裁判员、健身气功及田径项目三级裁判员。', N'1')
