INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'64', N'Kaylin Liang', N'升学指导部主任', N'教师团队', N'硕士（在读）：乔治华盛顿大学教育与人类发展专业

学士：加州大学伯克利分校社会学

大学升学指导课程：莱斯大学

拥有多年在知名国际学校担任升学指导老师和舍堂主任的工作经验。擅长指导学生的职业规划、申请文书的准备，举办大学申请介绍会，组织策划学生活动和竞赛，带领学生进行户外调研。Kaylin也曾任教于加州奥克兰市非盈利教育机构: College Track。', N'2')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'65', N'Letatia Brown Long', N'升学指导', N'教师团队', N'博士（在读）：瓦尔顿大学行政领导力与教学专业 

硕士：德克萨斯康戈迪亚大学教育管理专业  

拥有德州的升学指导和行政管理的专业资格证，具有16年的升学指导、学生导师和教学工作经验。她熟悉一般大学申请和特殊申请的过程，能够帮助学生发现兴趣，使之与学习相结合，从而申请到理想的大学。', N'2')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'66', N'周雅文', N'升学指导', N'教师团队', N'硕士：荷兰蒂尔堡大学传播与信息科学（信息技术人文学）专业

研究生期间获得荷兰政府的郁金香奖学金，曾学习英国贝尔法斯特女王大学的社会人类学研究生课程

具有多年留学咨询工作经验，曾在知名高中担任3年的升学指导。', N'2')
