INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'68', N'李斌', N'深圳中学AP 教师，物理学与经济学导师', N'深中导师团队', N'本科毕业于香港科技大学，获物理与数学双学位，研究生阶段先后就读于香港科技大学、香港大学，为物理学与经济学双专业硕士，现为深圳中学国际体系AP课程教师，一专多能，曾教授AP 物理、微积分、统计、经济、线性代数等多门国际课程。学术活动指导经验丰富，曾指导AMC 8/10/12 培训、 HiMCM 美国高中生数学建模培训、USIYPT，美国物理青年竞标赛，曾任美国十项全能（USAD）教练。', N'1')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'69', N'刘越', N'生物学科导师，生物教师', N'深中导师团队', N'毕业于陕西师范大学生物教育专业。现为深圳中学高级教师，生物学科组长。十数载培育桃李，多次获得教学能手、优秀班主任、勋贤奖称号。曾任三届生物奥赛辅导教练，同时致力于教学研究，在《生物学教学》、《中学生导报》等刊物上发表论文，主编《课时优化作业本》，参编《中学生物1 1》、《高三文理基础复习指导》等教参。', N'2')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'70', N'罗志文', N'深圳中学历史教师、历史科组长，历史学科导师', N'深中导师团队', N'现为深圳中学高级教师，从事中学历史教学25年，任历史科组长，为首批“广东省中学历史学科带头人”，深圳市高中历史学科中心组成员。不仅高考备考经验丰富、个人高考业绩突出，而且注重学科教学研究，先后参加了全国骨干教师的“国培班”学习、市教育局组织的美国纽约圣文森学院海外学习。', N'2')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'71', N'马靖', N'深圳中学地理教师，现任深中地理科组长，地理学科导师', N'深中导师团队', N'一个能让学生在课堂上感受轻松和快乐，并力图将复杂问题简单化处理，追求规范和逻辑的老师。教学中，注重学科思维和学科方法的培养，并侧重学科的实用价值，同时，努力维持学业成绩与思维方式、方法的平衡。', N'1')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'72', N'刘梅', N'深圳中学音乐教师，音乐学科导师', N'深中导师团队', N'现为中学高级教师，青年指挥家，任深圳中学音乐教师、合唱团指挥。为国际合唱联盟会员、中国合唱协会理事，并任深圳市合唱学会副理事长。一手指导深圳中学合唱团多次在国际、国内大赛中夺金，曾获国际奥林匹克合唱大赛世界冠军，本人获“奥林匹克合唱大赛优秀指挥大奖”；获全国及省市“优秀指挥奖”、“教师基本功大赛一等奖”、“深圳市十佳青年教师”、“深圳市中青年骨干教师”、是深圳市艺术教育领域的青年杰出代表。', N'2')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'73', N'花晶晶', N'深圳中学舞蹈教师，舞蹈学科导师', N'深中导师团队', N'现于深圳中学担任艺术课音乐与戏剧模块教师及舞蹈团指导教师，任深圳市舞蹈家协会理事。曾荣获“深圳市十佳青年教师”称号，编创舞蹈作品《奔向阳光》、《不能忘却的记忆》、《小小小小鸟》等，音乐剧作品《今年过年不回家》、《和谐计生关注民生》等，撰写歌词《咏春》、《玉生烟》、《心慌慌》等，多次荣获国家、省市级奖项。', N'2')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'74', N'吴健晖', N'深圳中学美术教师、艺术科组长,艺术科组导师', N'深中导师团队', N'高级教师，任国家教育部、中央美院、广东省骨干教师培训班特邀专家讲师。成果丰硕，曾获国际影艺联、美国摄影学会、德国摄影学会、中国国家地理摄影大赛金奖。从教以来，辅导的学生遍及国际一流美术院校和美术相关综合性大学，包括美国哈佛大学、加州理工大学、哥伦比亚大学、罗德岛艺术学院、帕森斯艺术学院、芝加哥艺术学院、英国皇家艺术学院、伦敦中央圣马丁艺术学院、中国中央美术学院、清华大学美术学院等。', N'1')
GO
GO
INSERT INTO [dbo].[teachers] ([id], [name], [job], [team], [info], [gender]) VALUES (N'75', N'郑路平', N'深圳中学体育教师、体育科组长,体育科组导师', N'深中导师团队', N'现为中学高级教师，田径专业教练、国家级裁判，曾获深圳市优秀教师，广东省、深圳市群众体育先进个人称号。', N'1')
GO
